class Solution {
    public boolean isMatch(String s, String p) {
        int n = p.length();
        int m = s.length();

        boolean[] prev = new boolean[m + 1];

        // Base Case
        prev[0] = true;

        for (int j = 1; j <= m; j++) {
            prev[j] = false;
        }

        for (int i = 1; i <= n; i++) {
            boolean[] cur = new boolean[m + 1];

            boolean flag = true;
            for (int k = 1; k <= i; k++) {
                if (p.charAt(k - 1) != '*') {
                    flag = false;
                    break;
                }
            }
            cur[0] = flag;

            for (int j = 1; j <= m; j++) {
                // If match found or '?' is found 
                if (p.charAt(i - 1) == s.charAt(j - 1) || p.charAt(i - 1) == '?') {
                    cur[j] = prev[j - 1];
                }

                // if it is a '*'
                else if (p.charAt(i - 1) == '*') {
                    cur[j] = prev[j] || cur[j - 1];
                }

                // No match found
                else cur[j] = false;
            }
            prev = cur;
        }

        return prev[m];
    }
}

