    class Solution {
    public:
        void GetLut(const char *p, int posp, int len, vector<int> *lut) {
            (*lut)[0] = -1;
            int pos = -1;
            for (int i = 1; i < len; ++i) {
                while (pos > -1 && p[posp+pos+1] != p[posp + i] && p[posp+pos+1] != '?' && p[posp+i] != '?')
                    pos = (*lut)[pos];
                if (p[posp+pos+1] == p[posp+i] || p[posp+pos+1] == '?' || p[posp+i] == '?') {
                    (*lut)[i] = ++pos;
                } else {
                    (*lut)[i] = -1;
                }
            }
