class Solution {
public:
     bool isMatch(string s, string p) {
        int m = s.size();
        int n = p.size();
        vector<vector<bool>> dp(m + 1, vector(n + 1, false));
        dp[0][0] = true;
        for (int j = 0; j < n && p[j] == '*'; j++) {
            dp[0][j + 1] = true;
        }

        for (int i = 1; i <= m; ++i) {
            for (int j = 1; j <= n; ++j) {
                if (p[j - 1] == '*') {
                    dp[i][j] = dp[i - 1][j] || dp[i][j - 1];
                } else if(s[i - 1] == p[j - 1] || p[j - 1] == '?') {
                    dp[i][j] = dp[i - 1][j - 1];
                }
            }
        }
        return dp[m][n];
        // return f(s, p, 0, 0);    
    }
    bool f(string& s, string &p, int i, int j) {
        if(j == p.size())
        return i == s.size();

        if(i == s.size())
        return p[j]=='*' && f(s, p, i, j+1);

        if(p[j] == '*')
        return f(s, p, i, j+1) || f(s, p, i+1, j);

        if(s[i] == p[j] || p[j] == '?')
        return f(s, p, i+1, j+1);

        return false;
    }
};
