        int n = p.size(),m = s.size();
        vector<bool>prev(m+1,0),curr(m+1,0);
        // case 1
        prev[0] = true;
        // case 2
        for(int j=1;j<=m;j++){
            prev[j] = false;
        }
        for(int i=1;i<=n;i++){
	        // case 3
            bool flag = true;
            for(int k=1;k<=i;k++){
                if(p[k-1] != '*'){
                    flag = false;
                    break;
                }
