            curr[0] = flag;
            for(int j=1;j<=m;j++){
                if(p[i-1] == s[j-1] || p[i-1] == '?'){
                    curr[j] = prev[j-1];
                }else if(p[i-1] == '*'){
                    curr[j] = curr[j-1] || prev[j];
                }else{
                    curr[j] = false;
                }
            }
            prev = curr;
        }
        return prev[m];
    }
