class Solution {
public:


    int helper(int i,int j,string s,string p,vector<vector<int>>& dp){

         if(i==0 && j==0 )    return true;

         if(i==0 || j==0 )    {

            if(i==0 && p[j-1]=='*') return helper(i,j-1,s,p,dp);  
