class Solution {
public:
    /*
        dp[i][j]==(-1) means dp[i][j] not calculated yet
        dp[i][j]==  1 means answer is true
        dp[i][j]==  0 means answer is false
    */

    bool search(string &s,string &pattern,int i,int j,vector<vector<int>>&dp){
        // i correspond to index in s
        //and j correspond to index in pattern

        if(i==s.size()&&j==pattern.size()){// both substrings are empty
            return true;
        }

        //We have reched at last character of pattern and it is *, then answer is true
        if(j==pattern.size()-1&&pattern[j]=='*'){
            return true;
        }

        //Either of them is empty but not both,then pattern can't be matched
        if(i==s.size()||j==pattern.size()){
            return false;
        }

        //Already calculated,then return value directly
        if(dp[i][j]!=-1){
            return dp[i][j];
        }

        /*
         current character in pattern is lowercase letter,
        then if both characters are matching then make function call with both indices incremented, otherwise if they are not matching return false

        if current character in pattern is '?', then make function call with both indices incremented

        otherwise current character is *. In that case, * can replace subsequence of any  length in original string.
        Therefore check for all possibilities of * 

        */

        if(pattern[j]!='*'&&pattern[j]!='?'){
            if(pattern[j]!=s[i]){
                return false;
            }
            return dp[i][j]=search(s,pattern,i+1,j+1,dp);
        }else if(pattern[j]=='?'){
            return dp[i][j]=search(s,pattern,i+1,j+1,dp);
        }else{
            bool ans=false;
            for(int k=i;k<=s.size();k++){
                ans=search(s,pattern,k,j+1,dp);
                if(ans){
                    break;
                }
            }
            return dp[i][j]=ans;
        }
    }

    bool isMatch(string s, string p) {
        string pattern;
        // consecutive sequence of * can be replaced with single *
        // Thereofre pattern will not have any consecutive *
        for(int i=0;i<p.size();i++){
            if(pattern.back()=='*'&&p[i]=='*'){
                continue;
            }
            pattern.push_back(p[i]);
        }
        vector<vector<int>>dp(s.size()+1,vector<int>(pattern.size()+1,-1));
        return search(s,pattern,0,0,dp);
    }
};
