class Solution(object):
    def isMatch(self, s, p):
        """
        :type s: str
        :type p: str
        :rtype: bool
        """
        ls = len(s)
        lp = len(p)
        if lp == 0: return ls == 0
        ss = i = j = 0
        star = -1
        while i < ls:
            if j < lp:
                if s[i] == p[j] or p[j] == "?":
                    i+=1
                    j+=1
                    continue
                elif p[j] == "*":
                    star = j
                    j = star +1
                    ss = i
                    continue
            if star != -1:
                j = star +1
                ss +=1
                i = ss
                continue
            return False
        while j < lp and p[j] == "*":
            j +=1
        return j == lp

