class Solution
{
public:
	bool isMatch(string s,string p)
	{
		int m = s.size();
		int n = p.size();
		int i = 0,j=0;
		int matchI = -1,starJ = -1;
		//使用贪婪算法 *先匹配0个字符，不行再匹配1个字符，2个字符...
		while(i<m)
		{
			if(j<n && p[j]=='*')
			{//如果p的当前字符是*,则*匹配0个字符
				matchI = i;
				starJ = j;
				j++;//*匹配0个字符，j后移与当前i匹配

			}
			else if(j<n &&(p[j] == '?' || p[j] == s[i]))
			{//如果当前字符匹配
				i++;
				j++;
			}
			else if(starJ >-1)
			{//如果p当前字符既不是*也不匹配，则i j 回退到上一次匹配的位置的下一位置
			 //这里matchI要+1，表示如果下次再进入该分支时,*多匹配一个字符（例如当前尝试*匹配一个字符不行，则后面尝试*匹配两个字符）
				i = matchI+1;
				j = starJ+1;
				matchI++;//*多匹配一个字符

			}
			else return false;
		}
		//遍历剩余的p中的*字符
		while(j<n && p[j] == '*') j++;

		//p后面剩余的字符是否全是*
		return j ==n;
	}
};
