class Solution {
    Boolean[][] memo;

    public boolean isMatch(String s, String p) {
        int M = s.length();
        int N = p.length();
        memo = new Boolean[M+1][N+1];
        return match(s, p, 0, 0);
    }

    private boolean match(String s, String p, int i, int j) {
        if (memo[i][j] != null) return memo[i][j];
        if (j == p.length()) {
            memo[i][j] = i == s.length();
            return memo[i][j];
        }

        char c = p.charAt(j);
        if (c == '*') {
            boolean skipStar = match(s, p, i, j+1);
            boolean useStar = i < s.length() && match(s, p, i+1, j);
            memo[i][j] = skipStar || useStar;
        } else if (c == '?') {
            memo[i][j] = i < s.length() && match(s, p, i+1, j+1);
        } else {
            memo[i][j] = i < s.length() && (s.charAt(i) == c) && match(s, p, i+1, j+1);
        }

        return memo[i][j];
    }
}
