class Solution:
    def isMatch(self, s, p):
        si, pi = 0, 0
        last_si, last_pi = None, None

        while si < len(s):
            if pi < len(p) and (s[si] == p[pi] or p[pi] == '?'):
                si += 1
                pi += 1
            elif pi < len(p) and p[pi] == '*':
                last_si = si + 1
                last_pi = pi + 1
                pi += 1
            elif last_pi:
                pi = last_pi
                si = last_si
                last_si += 1
            else:
                return False

        return p[pi:].count('*') == len(p) - pi
