def isMatch(self, s, p):
    m, n = len(s), len(p)
    if n - p.count('*') > m:   #avoid TLE
        return False
    dp = [False] * (n + 1)
    for i in xrange(m + 1):
        pre, dp[0] = dp[0], i == 0
        for j in xrange(1, n + 1):
            temp = dp[j]
            if p[j-1] == '*':
                dp[j] = pre or dp[j-1] or dp[j]
            else:
                dp[j] = pre and (p[j-1] in [s[i-1], '?'] ) 
            pre = temp
    return dp[-1]

