bool solve(string& str,string& pattern , int i , int j, vector<vector<int>>&dp)
{
    if(i<0 && j<0)
    {
        return true;
    }
    if(i>=0 && j<0)
    {
        return false;
    }
    if(j>=0 && i<0)
