        for(int i=1;i<=n;i++)dp[i][0]=false;
        int flag=1;
        for(int j=0;j<m;j++){ 
            if(p[j]=='*'&&flag)dp[0][j+1]=true;  // if p string starts with * like (***a?b or *aa) then we have true value for corresponding empty s string as *** in ***a?b will be considered empty.
            else{
                flag=0;
                dp[0][j+1]=false;
            }
        }

        for(int i=1;i<=n;i++){
            for(int j=1;j<=m;j++){
                if(s[i-1]==p[j-1]||p[j-1]=='?')dp[i][j]=dp[i-1][j-1];
                else if(p[j-1]=='*')dp[i][j]=dp[i-1][j]||dp[i][j-1];
