class Solution {

    public boolean isMatch(String s, String p) {
        return isMatchTDhelper(s, p, new int[s.length()][p.length()]);
    }

	public static boolean isMatchTDhelper(String s, String p, int[][] memo) {

		if (s.length() == 0 && p.length() == 0)
			return true;

		if (s.length() == 0 && p.length() != 0) {
			for (char ch : p.toCharArray()) {
				if (ch != '*')
					return false;
			}
			return true;
		}

		if (s.length() != 0 && p.length() == 0)
			return false;

		if (memo[s.length() - 1][p.length() - 1] != 0)
			return (memo[s.length() - 1][p.length() - 1]) == 2 ? true : false;

		char ch1 = s.charAt(0);
		char ch2 = p.charAt(0);

		boolean ans = false;

		String ros1 = s.substring(1);
		String ros2 = p.substring(1);

		if (ch2 == '?')
			ans = isMatchTDhelper(ros1, ros2, memo);

		else if (ch2 == '*') {
			ans = isMatchTDhelper(s, ros2, memo);
			ans = ans || isMatchTDhelper(ros1, p, memo);
		}

		else
			ans = (ch1 == ch2) ? isMatchTDhelper(ros1, ros2, memo) : false;

		memo[s.length() - 1][p.length() - 1] = (ans) ? 2 : 1;
		return ans;
	}

}

