class Solution {
public:
    bool isMatch(string s, string p) {
        return isMatchHelper(s.c_str(), p.c_str());
    }
private:
    bool isMatchHelper(const char *s, const char *p) {
        bool star = false;
        const char *str;
        const char *ptr;
        for (str = s, ptr = p; *str != '\0'; str++, ptr++) {
            switch(*ptr) {
                case '?' :
                    break; // str step for 1, ptr step for 1
                case '*' :
                    star = true;
                    s = str;
                    p = ptr;
                    while (*p == '*') p++; // skip continuous '*'
                    if (*p == '\0') return true;
                    str = s-1;
                    ptr = p-1;
                    break;
                default:
                    if (*str != *ptr) {
                        if (!star) return false;
                        s++;
                        str = s - 1;
                        ptr = p - 1;
                    }
            }
        }
        while (*ptr == '*') ptr++;
        return (*ptr == '\0');
    }
};
