class Solution {
    public boolean isMatch(String s, String p) {
        return dfs(s, p, 0, 0);
    }

    HashMap<String, Boolean> memo = new HashMap<>();
    public boolean dfs(String s, String p, int idx1, int idx2) {
        if (idx1 == s.length() && idx2 == p.length()) {
            return true;
        }

        if (!s.equals("") && !(idx2 < p.length() && p.charAt(idx2) == '*') && ((idx1 == s.length() && idx2 != p.length()) || (idx1 != s.length() && idx2 == p.length()))) {
            return false;
        }

        String key = ""+idx1+","+idx2;
        if (memo.containsKey(key)) {
            return memo.get(key);
        }

        boolean result = false;
        if (idx2 < p.length() && p.charAt(idx2) == '?') {
            result = dfs(s, p, idx1+1, idx2+1);
        } else if (idx2 < p.length() && p.charAt(idx2) == '*') {
            for (int i = idx1; i <= s.length(); i++) {
                if (dfs(s, p, i, idx2+1)) {
                    result = true;
                    break;
                }
            }
        } else if (idx2 < p.length() && idx1 < s.length() && p.charAt(idx2) == s.charAt(idx1)) {
            result = dfs(s, p, idx1+1, idx2+1);
        }

        memo.put(key, result);
        return result;
    }
}
