class Solution {
public:
    bool isMatch(string s, string p) {

        /**
         * dp[i][j]: Whether the substring of s ending at the index i can
         *           be matched by the subpattern of p ending at the index j.
         *
         *            | if s[i] == p[j] || p[j] == '?', dp[i - 1][j - 1]
         * dp[i][j] = | if p[j] == '*'                , dp[i - 1][j] || dp[i][j- 1]
         *            | otherwise                     , false
         */

        int len_s = s.length();
        int len_p = p.length();

        std::vector<std::vector<int>> 
            dp(len_s + 1, std::vector<int>(len_p + 1, false));
        dp[0][0] = true;

        for (int i = 1 ; i <= len_p ; ++i) {
            if (p[i - 1] != '*') {
                break;
            }
            dp[0][i] = true;
        }

        for (int i = 1 ; i <= len_s ; ++i) {
            for (int j = 1 ; j <= len_p ; ++j) {
                if (s[i - 1] == p[j - 1] || p[j - 1] == '?') {
                    dp[i][j] = dp[i - 1][j - 1];
                } else if (p[j - 1] == '*') {
                    dp[i][j] = dp[i][j - 1] || dp[i - 1][j];
                }
            }
        }

        return dp[len_s][len_p];
    }
};
