class Solution {
public:
    vector<int> res;
    vector<int> preorderTraversal(TreeNode* root) {
        if (!root) return res;
        stack<TreeNode*> s;
        s.push(root);
        TreeNode *currNode;
        while (s.size()) {
            currNode = s.top();
            s.pop();
            res.push_back(currNode->val);
            if (currNode->right) s.push(currNode->right);
            if (currNode->left) s.push(currNode->left);
        }
        return res;
    }
};
```

