class Solution {
public:
    vector<int> inorderTraversal(TreeNode* root) {
        vector<int> result{};
        stack<TreeNode*> stack{};
        TreeNode* prev = nullptr;

        if (root)
            stack.push(root);
        while (not stack.empty()) {
            TreeNode* node = stack.top();

            if (not prev or
                prev != node->left and prev != node->right) {
                while (true) {
                    // Pre-order region {
                    //   do_something();
                    // }
                    if (not node->left)
                        break;
                    node = node->left;
                    stack.push(node);
                }
            }

