//Preorder Traversal Using Morris Traversal
class Solution {
public:
    vector<int> preorderTraversal(TreeNode* root) {
        vector<int> ans;
        TreeNode* curr=root;
        while(curr){
            if(!curr->left){
                ans.push_back(curr->val);
                curr=curr->right;
            }
            else{
                TreeNode* pre=curr->left;
                while(pre->right&&pre->right!=curr){
                    pre=pre->right;
                }
                if(pre->right==NULL){
                    pre->right=curr;
                    ans.push_back(curr->val);
                    curr=curr->left;
                }
                else{
                    pre->right=NULL;
                    curr=curr->right;
                }
            }
        }
        return ans;
    }
};
