class Solution {
public:
    vector<int> preorderTraversal(TreeNode* root) {
        vector<int> ans ;
        TreeNode *curr = root, *pre;

        while(curr != NULL){
            if(curr->left == NULL){
                ans.push_back(curr->val);
                curr = curr->right;
            }
            else{
                pre = curr->left;
                while(pre->right != NULL && pre->right != curr)
                    pre = pre->right;

                if(pre->right == NULL){
                    ans.push_back(curr->val);
                    pre->right = curr;
                    curr = curr->left;
                }
                else{
                    pre->right = NULL;
                    curr = curr->right;
                }
            }
        }
        return ans;
    }
};
