class Solution {
public:
    void reverseString(vector<char>& s) {

        stack<char> tempStack;
        int i=0;
        while(i<s.size()){
            tempStack.push(s[i++]);
        }

        s.clear();
        while(!tempStack.empty()){
            s.push_back(tempStack.top());
            tempStack.pop();
        }
    }
};
