    let p1 = Math.floor(s.length/2)-1;
    let p2 = s.length % 2 == 0 ? Math.floor(s.length/2) : Math.floor(s.length/2)+1;
    while(p1>=0){
        [s[p1], s[p2]] = [s[p2], s[p1]];
        p1--;
        p2++;
    }
    return s;
};
