    void reverse (string &str, int start, int end)
    {
        // base case - condition to stop recursion - when the string is completely swapped
        if (start > end)
        {
            return ; 
        }

        // ek case solve krdo baaki recursion lagado 
        swap(str[start], str[end]) ;
        start ++; 
        end --;

        // recursive call
        reverse(str, start , end) ;
    }
