//Method 1(Bruteforce Approach)

class Solution {
    public int[] findErrorNums(int[] arr) 
    {
       Arrays.sort(arr);
       int n=arr.length;
	   int[] res=new int[2]; 

       for(int i=0;i<n;i++)
         if(arr[i]==arr[i+1])
         {
           res[0]=arr[i]; //repeating
           break;
         }

       for(int i=1;i<=n;i++)
         if(binarySearch(arr,i)==false)
         {
            res[1]=i; //missing
            break;
         }

        return res;
    }

    public static boolean binarySearch(int[] arr,int key)
    {
        int low=0,high=arr.length-1;
        while(low<=high)
        {
           int mid=(low+high)/2; 
           if(arr[mid]==key)
             return true;
           else if(key>arr[mid])
             low=mid+1;
           else if(key<arr[mid])
              high=mid-1;
        }
        return false;
    }
}
