//Method 6 (Optimal Approach) (For Best Explanation : https://youtu.be/MvklwzVz654 )

class Solution {
    public int[] findErrorNums(int[] arr) 
    {
          int n=arr.length,x=0,y=0,xXory=0,rmsbm;
          int[] res=new int[2];

          for(int i=1;i<=n;i++)
             xXory=xXory^i;

          for(int val:arr)
             xXory=xXory^val;

          rmsbm=rMSBM(xXory);

          for(int val:arr)
            if((val & rmsbm)==0)
               x=x^val;
            else
               y=y^val;

          for(int i=1;i<=n;i++)
            if((i & rmsbm)==0)
               x=x^i;
            else
               y=y^i;

          for(int i=0;i<n;i++)
            if(arr[i]==x)
            {
                res[0]=x; //Repeating
                res[1]=y; //Missing
                break;

            }
            else if(arr[i]==y)
            {
                res[0]=y; //Repeating
                res[1]=x; //Missing
                break;
            }

        return res;
    }

    public static int rMSBM(int n)
   {
       int rsbm= n & twosCompliment(n);
       return rsbm;
   }

   public static int twosCompliment(int n)
   {
       return -n;//(~n+1);
   }
}
