             if(arr[correctIndex]==arr[i])
	              i++;
	         else //if arr[i] is not at its correct index
             {
               int temp=arr[correctIndex];
               arr[correctIndex]=arr[i];
               arr[i]=temp;
             }      
           }  

           for (i=0; i<n; i++) 
             if(arr[i]!=i+1)
             {
               res[0]=arr[i]; //repeating
               res[1]=i+1; //missing
               break;
             }

        return res;
    }
}
