class Solution {
public:
    vector<int> findErrorNums(vector<int>& nums) {
        vector<int> res = {0, 0};
        unordered_map<int, int> count;
        long sum = 0;

        for(int i = 0; i < nums.size(); i++){
            if(count.find(nums[i]) == count.end()){
                count[nums[i]] = 1;
                sum += nums[i];
            }
            else{
                count[nums[i]]++;
                res[0] = nums[i];
            }
        }

        res[1] = nums.size()*(nums.size()+1)/2 - sum;

        return res;
    }
};
