class Solution:
    def findErrorNums(self, nums: List[int]) -> List[int]:
        # initialize variables
        n = len(nums)
        seen = set()
        missing = -1
        duplicate = -1

        # loop through nums array
        for num in nums:
            # check if the number is already seen
            if num in seen:
                # if yes, then it is the duplicate number
                duplicate = num
            else:
                # if not, add it to the seen set
                seen.add(num)

        # loop through numbers 1 to n and find the missing number
        for i in range(1, n+1):
            if i not in seen:
                missing = i
                break

        # return the result as a list
        return [duplicate, missing]
