public int[] findErrorNums(int[] nums) {
	int n = nums.length;
	int realSum = 0, duplicate = -1, targetSum = n * (n + 1) / 2;
	for (int i = 0; i < nums.length; i++) {
		int num = Math.abs(nums[i]);
		int index = num % n;
		realSum += num;
		if (nums[index] < 0) {
			duplicate = num;
		} else {
			nums[index] = -nums[index];
		}
	}
	return new int[] {duplicate, targetSum - (realSum - duplicate)};
}
