class Solution {
public:
    vector<int> findErrorNums(vector<int>& nums) {
        long long n = (long long)nums.size();
        long long sum = (n*(n+1))/2;
        long long calSum = 0;
        for(auto it:nums)
            calSum+=it;
        long long subbe = sum - calSum;
        long long sumsq = (n*(n+1)*(2*n+1))/6;
        long long calSumSq = 0;
        for(auto it:nums)
            calSumSq+=(long long)(it*it);
        long long b2e2 = sumsq - calSumSq;
        long long addbe = b2e2/subbe;
        long long b = (addbe+subbe)/2;
        long long e = (addbe-subbe)/2;
        return {(int)e, (int)b};

    }
};
