vector<int> findErrorNums(vector<int>& nums) {
	int X = 0, Y;
	for(int i = 0; i < nums.size(); i++){
		int a = abs(nums[i]);
		X ^= (i+1) ^ a;
		if(nums[a-1] < 0) Y = a;
		else nums[a-1] = -nums[a-1]; 
	}
	return {Y, X^Y};
}
