class Solution {
public:
    vector<int> findErrorNums(vector<int>& nums) {
        int n=nums.size();
        vector<bool>v(n+1,false);
        int ans=-1;
		//mark the numbers as visited. if already visited that is the one repeating
        for(auto x:nums){
            if(v[x])
                ans=x;
            else
                v[x]=true;
        }
		//The number that is not visited is considered to be the one that is missing
        for(int i=1;i<=n;i++){
            if(!v[i]){
                return {ans,i};
            }
        }
        return {ans,ans};
    }
};
```

