var findErrorNums = function (nums) {
  let i = 0;
  while (i < nums.length) {
    const idx = nums[i] - 1;
    if (nums[idx] !== nums[i]) {
      [nums[idx], nums[i]] = [nums[i], nums[idx]];
    } else {
      i++;
    }
  }

  for (let i = 0; i < nums.length; i++) {
    const expected = i + 1;
    if (expected === nums[i]) continue;

    return [nums[i], expected];
  }
};
