    const output = [0,0];

    for (let i = 0; i < nums.length; i++) {
        const num = nums[i];
        if (num === cacheValue) output[0] = num;
        if (num === cacheValue + 2) output[1] = num - 1;
        cacheValue = num;

        if (output[0] && output[1]) break;
    }

    if (!output[1]) output[1] = nums[nums.length - 1] + 1;

    return output;
};
