class Solution:
#     O(n) || O(1)
# Runtime: 236ms 79.68% || Memory: 15.3mb 93.57%
    def findErrorNums(self, nums: List[int]) -> List[int]:
        if not nums:
            return nums

        def cyclicSort(array):
            i = 0
            while i < len(array):
                check = array[i] - 1

                if array[i] != array[check]:
                    array[i], array[check] = array[check], array[i]
                else:
                    i += 1

        cyclicSort(nums)

        result = []

        for i in range(len(nums)):
            if i + 1 != nums[i]:
                result += [nums[i], i + 1]

        return result

