        for i in range(len(word)-1, -2, -1):
            newString = word[i+1:] + '#' + word
            node = self.trie
            node.idx = idx

            for letter in newString:
                if letter not in node.children:
                    node.children[letter] = Trie()
                node = node.children[letter]
                node.idx = idx


