class TrieNode{
public :

    vector<TrieNode*>children;
    int index;

    TrieNode(){
        children = vector<TrieNode*>(27, NULL);
        index = -1;
    }
    // create destructor to avoid any memory leak
    ~TrieNode(){
        for (int i = 0; i < 26; i++) {
            if (children[i]) {
                delete children[i];
            }
        }
    }
};

// Class to do the basic operations like insert, searching etc.
class Trie{
public:

    TrieNode* root;
    Trie(){
        root = new TrieNode();
    }

    void insert(string &s, int idx)
    {        
        TrieNode* cur = root;
        for(int i=0;i<s.size();i++)
        {
            if(cur->children[s[i]-'a'] == NULL)
                cur->children[s[i]-'a'] = new TrieNode();

            cur = cur->children[s[i]-'a'];
            cur->index = idx;
        }
    }

    int search(string &word)
    {
        TrieNode* cur = root;
        for(int i = 0; i < word.size(); i++){
            cur = cur->children[word[i] - 'a'];
            if(!cur) 
                return -1;
        }
        return cur->index;
    }
};

class WordFilter {
public:    

    Trie *trie = new Trie();
    WordFilter(vector<string>& words) 
    {    
        int idx = 0;
        string s = "";

        for(auto &word : words) 
        {
            s = "{" + word;
            trie->insert(s, idx);

            for(int i=word.size()-1; i>=0; i--) 
            {
                s = word[i] + s;
                trie->insert(s, idx);
            }
            idx++;
        }
    }

    int f(string prefix, string suffix) {

        string match = suffix + "{" + prefix;
        int ansIdx = trie->search(match);
        return ansIdx;
    }
};
