    {
        root->idx_list.push_back(idx); // To Deal with empty string case

        TrieNode* cur = root;
        for(int i=0;i<s.size();i++)
        {
            if(cur->children[s[i]-'a'] == NULL)
                cur->children[s[i]-'a'] = new TrieNode();
            cur = cur->children[s[i]-'a'];

            cur->idx_list.push_back(idx);
        }
        cur->isEnd = true;
    }

    vector<int> search(string &word)
    {
        TrieNode* cur = root;
        for(int i = 0; i < word.size(); i++){
            cur = cur->children[word[i] - 'a'];
            if(!cur) 
                return {};
        }
        return cur->idx_list;
    }
};

class WordFilter {
public:

    // Using 2 Trie
    Trie* forward = new Trie();
    Trie* backward = new Trie();
    WordFilter(vector<string>& words) 
    {
        for(int i=0;i<words.size();i++)
        {
            forward->insert(words[i], i);
            reverse(words[i].begin(), words[i].end());
            backward->insert(words[i], i);
        }
    }

    // Dry run for input 
    // dictionary = ["apple"], prefix = "ap", suffix = "le" 
    int f(string prefix, string suffix) 
    {
        vector<int> pre = forward->search(prefix);
        reverse(suffix.begin(), suffix.end()); // suffix = "el"
        vector<int> post = backward->search(suffix);

        int i = pre.size() - 1;
        int j = post.size() - 1;
        // start from last, because we want largest index
        while(i >= 0 && j >= 0)
        {
            if(pre[i] == post[j]) 
                return pre[i]; // largest index out of all valid equal indices
            else if(pre[i] > post[j]) 
                i--;
            else 
                j--;
        }
        return -1;    
    }
};
