        trie = allocate();
        for(int weight=0;weight<words.size();weight++){

            string str = words[weight];
            string word = "{" + str;
            insert(word,trie,weight);

            for(int i=str.length()-1;i>=0;i--){
                word = str[i] + word;
                insert(word,trie,weight);
            }
        }        
    }

    /*standard search operation in trie*/
    int f(string prefix, string suffix) {
        string word = suffix + "{" + prefix;
        struct node* root = trie;

        for(int i=0;i<word.length();i++){
            int idx = word[i] - 'a';
            if(root->child[idx] == NULL)
                return -1;
            root = root->child[idx];
        }
        return root->weight;
    }
};
