        String word = words[index];

        for(int i = 1; i <= word.length(); i++){

            String prefix = word.substring(0, i);
            prefixes.putIfAbsent(prefix, new ArrayList<Integer>());
            prefixes.get(prefix).add(index);
        }
    }

    private void addSuffix(int index){
        String word = words[index];

        for(int i = word.length() - 1; i >= 0; i--){

            String suffix = word.substring(i, word.length());
            suffixes.putIfAbsent(suffix, new ArrayList<Integer>());
            suffixes.get(suffix).add(index);
        }
    }

    public int f(String prefix, String suffix) {
        if(!prefixes.containsKey(prefix) || !suffixes.containsKey(suffix)) return -1;

        //find matched max indices
        return getMaxIndex(prefixes.get(prefix), suffixes.get(suffix));
    }

    private int getMaxIndex(List<Integer> indices1, List<Integer> indices2){
        //As both prefix and suffix indices are in decresing order
        //here we can use the two pointer, to find the matched max index

        int i = 0;
        int j = 0;
        while(i < indices1.size() && j < indices2.size()){

            if(indices1.get(i) - indices2.get(j) == 0) return indices1.get(i);

            if(indices1.get(i) > indices2.get(j)){
                i++;
            }else if(indices1.get(i) < indices2.get(j)){
                j++;
            }
        }

        return -1;
    }
}

