# Solution 1: Trie Data Structure (Used only One Trie)

```
class Trie {

    Trie[] links;
    List<Integer> prefixIndices;
    List<Integer> suffixIndices;

    public Trie () {

        links = new Trie[26];
        prefixIndices = new ArrayList<> ();
        suffixIndices = new ArrayList<> ();
    }
}

class WordFilter {

    Trie root;

    public void insertWord (String word, int index, boolean flag) {

        Trie node = root;

        for (char c : word.toCharArray ()) {
            if (node.links[c - 'a'] == null) {
                node.links[c - 'a'] = new Trie ();
            }

            node = node.links[c - 'a'];
            if (flag) {
                node.prefixIndices.add (index);
            }
            else {
               node.suffixIndices.add (index); 
            }
        }
    }

    public List<Integer> searchWord (String word, boolean flag) {

        Trie node = root;

        for (char c : word.toCharArray ()) {
            node = node.links[c - 'a'];
            if (node == null) {
                return null;
            }
        }

        return flag ? node.prefixIndices : node.suffixIndices;
    }

    public WordFilter(String[] words) {

        root = new Trie ();

        for (int index = 0; index < words.length; index++) {
            insertWord (words[index], index, true);
            insertWord (new StringBuilder (words[index]).reverse ().toString (), index, false);
        }
    }

    public int f(String prefix, String suffix) {

        List<Integer> prefixIndices = searchWord (prefix, true);

        if (prefixIndices == null || prefixIndices.size () == 0) {
            return -1;
        }

        List<Integer> suffixIndices = searchWord (new StringBuilder (suffix).reverse ().toString (), false);

        if (suffixIndices == null || suffixIndices.size () == 0) {
            return -1;
        }

        int i = prefixIndices.size () - 1, j = suffixIndices.size () - 1;

        while (i >= 0 && j >= 0) {
            if (Objects.equals (prefixIndices.get (i), suffixIndices.get (j))) {
                return prefixIndices.get (i);
            }
            else if (prefixIndices.get (i) > suffixIndices.get (j)) {
                i--;
            }
            else {
                j--;
            }
        }

        return -1;
    }
}
