```cpp
class WordFilter {
public:
    Trie *root = new Trie(), *revRoot = new Trie(), *curr;

    void populateTrie(string word, Trie *root, int i) {
        curr = root;
        for (char c: word) {
            if (!curr->children[c]) curr->children[c] = new Trie();
            curr = curr->children[c];
            curr->possiblePos.insert(i);
        }
    }

    WordFilter(vector<string>& words) {
        // populating our trie
        for (int i = 0, lmt = words.size(); i < lmt; i++) {
            string word = words[i];
            populateTrie(word, root, i);
            reverse(begin(word), end(word));
            populateTrie(word, revRoot, i);
        }
    }

    int f(string prefix, string suffix) {
        curr = root;
        // finding matching words by prefix
        for (char c: prefix) {
            curr = curr->children[c];
        }
        // getting all the possible words
        auto posA = curr->possiblePos;
        // preparing curr and suffix and finding matching words by suffix
        curr = revRoot;
        reverse(begin(suffix), end(suffix));
        for (char c: suffix) {
            curr = curr->children[c];
        }
        // getting all the possible words
        auto posB = curr->possiblePos;
        // getting the smallest one of them first
        if (posA.size() > posB.size()) swap(posA, posB);
        for (int pos: posA) if (posB.find(pos) != end(posB)) return pos;
        return -1;
    }
};
```

