};

class WordFilter {
public:
    Trie *root = new Trie(), *revRoot = new Trie(), *curr;
    int res;

    void dfs(Trie *root, string suffix) {
        // leaving if we cannot find any better result
        if (root->maxPos < res) return;
        // updating res everytime we encounter a word end with a word that matches our suffix
        if (root->pos != -1 && 0 == root->fullWord.compare(root->fullWord.size() - suffix.size(), suffix.size(), suffix)) res = max(res, root->pos);
		// checking all the existing children
        for (int i = 97; i < 123; i++) {
            auto child = root->children[i];
            if (child) dfs(child, suffix);
        }
    }

    void populateTrie(string word, Trie *root, int i) {
        curr = root;
        for (char c: word) {
            if (!curr->children[c]) curr->children[c] = new Trie();
            curr = curr->children[c];
            curr->maxPos = max(curr->maxPos, i);
        }
        curr->pos = i;
        curr->fullWord = word;
    }

    WordFilter(vector<string>& words) {
        // populating our trie
        for (int i = 0, lmt = words.size(); i < lmt; i++) {
            string word = words[i];
            populateTrie(word, root, i);
            reverse(begin(word), end(word));
            populateTrie(word, revRoot, i);
        }
    }

    int f(string prefix, string suffix) {
        if (prefix.size() < suffix.size()) {
            curr = revRoot;
            reverse(begin(prefix), end(prefix));
            reverse(begin(suffix), end(suffix));
            swap(prefix, suffix);
        }
        else curr = root;
        // finding matching words
        for (char c: prefix) {
            curr = curr->children[c];
        }
        // resetting res
        res = -1;
        // checking suffix
        dfs(curr, suffix);
        return res;
    }
};
