class TrieNode{
    public:
        vector<TrieNode*> children;
        int weight;
        TrieNode():children(27) //26 for the English alphabet + 1 for the delimiter: {
        {
            weight = 0;
        }
        ~TrieNode()
        {
            children.clear();
        }
};

class WordFilter {
private:
    TrieNode *trie;
public:
    WordFilter(vector<string>& words) {
        trie = new TrieNode();
        int size = words.size();
        for(int weight = 0; weight < size; weight++) //Iterate through all the words in the dictionary, keep track of the index via weight and update the TrieNodes where applicable
        {
			//Note: '{' comes right after 'z' in ASCII
            string word = words[weight] + "{"; //Eg: test{ 
            //cout << word << endl;
            int len = word.length();
            for(int i = 0; i < len; i++) //for each character in the word
            {
                TrieNode *current = trie;
                //cout << " new trie entry ";
                current->weight = weight;
                for(int j = i; j < 2*len-1; j++) //Eg: strlen("test{test") = 9 which translates to j < 2*strlen(word i.e. "test{")-1
                {
                    int k = word[j%len]-'a';
                    //cout << "k : " << k << "new weight: " << weight;
                    if(current->children[k] == nullptr)
                        current->children[k] = new TrieNode();
                    current = current->children[k];
                    //cout << " old weight: " << current->weight << endl;
                    current->weight = weight; //this is what updates the weight of the common nodes in the Trie structure if you happen to find a word meeting the suffix&prefix match criteria at a later index
                }
            }
        }
    }

    int f(string prefix, string suffix) {
        TrieNode *current = trie;
        string input = suffix + '{' + prefix;
        for(auto letter: input)
        {
            if(current->children[letter-'a'] == nullptr)
                return -1;
            current = current->children[letter-'a'];
        }
        return current->weight;
    }
};
