    public class Trie {

	    private class Node {

		char data;
		int idx;
		Node[] children;
		boolean isTerminal;

		public Node(char data, int idx, boolean isTerminal) {
			this.data = data;
			this.idx = idx;
			this.isTerminal = isTerminal;
			this.children = new Node[27];
		}

	}

	private Node root;

	// O(1)
	public Trie() {
		this.root = new Node('\0', -1, false);
	}

	// O(word.length^2)
	public void addWord(String word, int idx) {
		LinkedList<Character> prefix = new LinkedList<Character>();
		int len = word.length();
		for (int i = len - 1; i >= 0; i--) {
			prefix.addFirst(word.charAt(i));
			addWordHelper(prefix.toString() + "{" + word, idx);
		}
	}

	// O(word.length())
	public void addWordHelper(String word, int idx) {
		Node curr = root;
		for (char ch : word.toCharArray()) {
            if(ch - 'a' < 0)
                continue;
			Node temp = curr.children[ch - 'a'];
			if (temp == null) {
				temp = new Node(ch, idx, false);
				curr.children[ch - 'a'] = temp;
			}
			temp.idx = idx;
			curr = temp;
		}
		curr.isTerminal = true;
	}

	// O(word.length)
	public int searchWord(String word) {
		Node curr = root;
		for (char ch : word.toCharArray()) {
			Node temp = curr.children[ch - 'a'];
