class node{
    public:
        node *next[27];
        int last;  // This stores the largest i for the words[i] that utilises *this particular node* in its sequence 
        node(){
            memset(next,0,sizeof(next));
            last=-1;  
        }
};
class trie{
    public:
    node *root=new node();
    void insert(string &word, int i){
        node *temp=root;
        for(auto c:word){
            if(!temp->next[c-'a']){
                temp->next[c-'a']=new node();
            }
            temp=temp->next[c-'a'];
            temp->last=i;  // Since calls to this function are made in the order of increasing {i}, the node will eventually have the largest {i} associated with it
        }
    }
    bool search(string &word, int &i){
        node* temp=root;
        for(auto c:word){
            if(!temp->next[c-'a']){ i=-1; return false; }
            temp=temp->next[c-'a'];
            i=temp->last; 
        }
        return true;
    }
};
class WordFilter {
public:
    trie* t=new trie();
    WordFilter(vector<string>& words) {
        int i=0;
        for(int i=0; i<words.size(); i++){
            string w=words[i];
            for(int j=0; j<w.length(); j++){
                string r=w.substr(j)+'{'+w;
                t->insert(r,i);
            }
        }        
    }

    int f(string pref, string suff) {
        int ans=-1;
        string s=suff+'{'+pref;
        t->search(s,ans);
        return ans;
    }
};
