    Trie trie = new Trie();

    public WordFilter(String[] words) {

        int len = words.length;
        for (int i = 0; i < len; i++) {            
            String str = words[i];
            int n = str.length();
            for (int j = 0; j <= n; j++) {                
                String cur = str.substring(j) + '{' + str;
                trie.insert(cur, i);
            }
        }

    }

    public int f(String prefix, String suffix) {
        String cur = suffix + '{' + prefix;
        return trie.startsWith(cur);
    }
}

