class WordFilter {
        TreeMap<String, Integer> prefixMap = new TreeMap<>();
        TreeMap<String, Integer> suffixMap = new TreeMap<>();

        public WordFilter(String[] words) {
            for (int i = 0; i < words.length; i++) {
                prefixMap.put(words[i], i);
                suffixMap.put(new StringBuilder(words[i]).reverse().toString(), i);
            }
        }

        public int f(String prefix, String suffix) {
            int pos = -1;
            Set<Integer> set = new HashSet<>();
            for (Map.Entry<String, Integer> entry : prefixMap.subMap(getLowKey(prefix), getHighKey(prefix)).entrySet()) {
                if (entry.getKey().startsWith(prefix)) {
                    set.add(entry.getValue());
                }
            }
            suffix = new StringBuilder(suffix).reverse().toString();
            for (Map.Entry<String, Integer> entry : suffixMap.subMap(getLowKey(suffix), getHighKey(suffix)).entrySet()) {
                if (entry.getKey().startsWith(suffix) && set.contains(entry.getValue())) {
                    pos = Math.max(pos, entry.getValue());
                }
            }
            return pos;
        }

